mapper.select:
select
ROLE_ID,operator_id,creator_id,ROLE_NAME,VERSION,ROLE_DESC,STS,date_format(OPERATE_DATE,'%Y-%m-%d %H:%i:%s') OPERATE_DATE ,date_format(CREATE_DATE,'%Y-%m-%d %H:%i:%s') CREATE_DATE  ,operator,creator,ROLE_CODE from
SYS_ROLE where ROLE_ID=#{roleId}

select S.ROLE_ID,s.operator_id,s.creator_id, S.ROLE_NAME, S.ROLE_DESC, S.STS, date_format(OPERATE_DATE,'%Y-%m-%d %H:%i:%s') OPERATE_DATE ,date_format(CREATE_DATE,'%Y-%m-%d %H:%i:%s') CREATE_DATE ,operator,creator,S.ROLE_CODE from SYS_ROLE S where 1=1 AND S.STS='A' and s.role_id!=1
and S.ROLE_NAME like '%${condition.roleName}%'
and S.ROLE_CODE like '%${condition.roleCode}%'
order by ${page.sortName}
${page.sortOrder}
